import { FC, useCallback, useEffect, useState } from "react";
import Layout from "../components/Layout";
import styled from "styled-components";
import { mediaQuery } from "../GlobalStyle";
import GroupListItem from "../components/groups/GroupListItem";
import { useNavigate } from "react-router";
import { BoxWrapper } from "../style/theme";
import { AddBtn, BoxHeader } from "../style/theme";
import { getGroupsApi } from "../lib/api/group";
import { useDispatch, useSelector } from "react-redux";
import { getGroups } from "../redux/actions/Group";
import { RootState } from "../redux/reducers";

const GroupIndexPage: FC = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const groups = useSelector((store: RootState) => store.group.groups);

  const handleClickLink = useCallback(() => {
    navigate("add");
  }, [navigate]);

  useEffect(() => {
    getGroupsApi().then((res) => {
      dispatch(getGroups(res.data));
    });
  }, [dispatch]);

  return (
    <Layout title="그룹">
      <BoxHeader>
        <span>그룹</span>
        <AddBtn onClick={handleClickLink}>새그룹추가</AddBtn>
      </BoxHeader>
      <BoxWrapper>
        {groups
          ? groups.map((group) => (
              <GroupListItem
                id={group._id}
                desc={group.groupDesc}
                emoji={group.groupEmoji}
                name={group.groupName}
                key={group._id}
              />
            ))
          : null}

        {/* <AddButton
          onClick={handleClickLink}
          iconSrc={"data:image/png;base64,"}         
          title={"신규그룹추가"} /> */}
      </BoxWrapper>
    </Layout>
  );
};

export default GroupIndexPage;
